import React, { useState, useEffect, useRef  } from "react";
import { csv } from 'd3';

function App() {
  // Message Handling
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([ ]);

  function sendMessage() {
    if (message === "") {
      return;
    }

    // Add message to chat log
    setMessages((prevMessages) => [...prevMessages, { sender: "You", text: message }]);
    setMessage("");

    // Response
    setTimeout(() => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "System", text: "I am a simple bot. I don't have any real responses yet!" },
      ]);
    }, 1000);
  }

  function handleMessage(e) {
    if (e.key === "Enter") {
      e.preventDefault();
      sendMessage();
    }
  }

  // Ref to the chat log container
  const chatLogRef = useRef(null);

  // Scroll to the bottom of the chat log
  const scrollToBottom = () => {
    if (chatLogRef.current) {
      chatLogRef.current.scrollTop = chatLogRef.current.scrollHeight;
    }
  };

  // Scroll down whenever a new message is added
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // CSV Handling
  const fileInputRef = useRef(null);
  const [data, setData] = useState(null)
  const [csvMessage, setCsvMessage] = useState('Click / drag & drop to upload a CSV file!');

  const processFiles = (files) => {
    if (files.length > 0) {
      const file = files[0];
      if (file.type !== 'text/csv') {
        setCsvMessage('Error. Please drop a valid CSV file.');
        return;
      }

      // Read the CSV file
      const reader = new FileReader();
      reader.onload = (e) => {
        const csvData = e.target.result;

        // Parse the CSV data using d3
        csv(csvData).then((parsedData) => {
          setData(parsedData);
          setCsvMessage(`CSV uploaded! Filename: ${file.name}`); // Clear error if parsing is successful
        }).catch((err) => {
          setCsvMessage('Error parsing CSV file.');
        });
      };
      reader.readAsText(file);
    }
  }

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const files = event.dataTransfer.files;
    processFiles(files);
  };

  const handleClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const files = event.target.files;
    processFiles(files);
  };



  // Site
  return (
    <div className="flex items-center justify-center h-screen">
      <div className="px-0 py-0 max-w-5xl w-full">
        {/* Header */}
        <h1 className="text-5xl font-bold mb-4 text-left">Data Visualization Assistant</h1>

        {/* CSV */}
        <div 
          className="flex items-center justify-center h-40 border border-black rounded my-10"
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onClick={handleClick}
        >
          <h3 className="font-medium italic">{csvMessage}</h3>
          <input
            type="file"
            accept=".csv"
            ref={fileInputRef}
            onChange={handleFileChange}
            className="hidden"
          >

          </input>
        </div>

        {/* CSV Preview */}
        {data && (
        <div className="mt-5 p-4 border border-gray-300 rounded shadow-lg">
          <h4 className="text-lg font-semibold">CSV Preview:</h4>
          <table className="min-w-full border-collapse border border-gray-300">
            <thead>
              <tr>
                {Object.keys(data[0]).map((key) => (
                  <th key={key} className="border border-gray-300 p-2">{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, index) => (
                <tr key={index} className="hover:bg-gray-100">
                  {Object.values(row).map((value, i) => (
                    <td key={i} className="border border-gray-300 p-2">{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        )}

        {/* Chatlog */}
        <div ref={chatLogRef} className="h-80 overflow-y-auto border px-4 py-8 rounded-lg border-black shadow-lg">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`chat ${msg.sender === "You" ? "chat-end" : "chat-start"}`}
            >

              <div className="chat-image avatar">
                <div className="w-10 flex items-center justify-center text-3xl">
                  {msg.sender === "System" ? (
                    <span role="img" aria-label="robot emoji">
                      🤖
                    </span>
                  ) : (
                    <span role="img" aria-label="human emoji">
                      🧑
                    </span>
                  )}
                </div>
              </div>

              <div className="chat-header">{msg.sender}</div>
              <div className="chat-bubble">{msg.text}</div>
            </div>
          ))}
        </div>

        {/* Input Box */}
        <div className="mt-5 flex gap-2">
          <input
            type="text"
            placeholder="Type your message here"
            value={message}
            className="input input-bordered w-full"
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleMessage}
          />
          <button className="btn" onClick={sendMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
